import React, { useState } from 'react';
import { Route, Switch, Redirect } from 'react-router-dom';
import Header from './Header';
import Mesto from './Mesto';
import Form from './Form';

function App() {
  const [loggedIn, setLoggedIn] = useState(false);

  return (
    <div className="page">
    <Switch>
      <Route path="/sign-up">
        <Header>
          <a href="/sign-in" className="header__link">Войти</a>
        </Header>
        <Form title="Регистрация" button="Зарегистрироваться"><a href="/sign-in" className="main-form__link">Уже зарегистрированы? Войти</a></Form>
      </Route>
      <Route path="/sign-in">
        <Header>
          <a href="/sign-up" className="header__link">Регистрация</a>
        </Header>
        <Form title="Вход" button="Войти" />
      </Route>
      <Route path="/mesto">
        <Mesto />
      </Route>
      <Route exact path="/">
        {loggedIn ? <Redirect to="/test" /> : <Redirect to="/sign-in" />}
      </Route>
    </Switch>
    </div>

  );
}

export default App;
